<?php

defined('_JEXEC') or die('Restricted access');

class plgSystemBlackRibbon extends JPlugin
{

	function onBeforeCompileHead()
  {

		$app = JFactory::getApplication();

		if ($app->isSite()) {
			$document = JFactory::getDocument();
			if(file_exists(dirname(__FILE__).'/assets/css/ribbon.css')){
				$document->addStyleSheet(JURI::root().'plugins/system/blackribbon/assets/css/ribbon.css');
			}

		}
   }

	function onAfterRender()
  {
  	$app = JFactory::getApplication();

  	if ($app->getCfg('offline') == 0) {

  		//Test non administration
      if (!$app->isSite()) {
        return;
      }

      $ondisplay = $this->params->get('ondisplay');
      $homemenu =& $app->getMenu()->getActive()->home;
      if ($ondisplay && !$homemenu) {
        return;
      }
      $jinput = JFactory::getApplication()->input;
      $tmpl = $jinput->get('tmpl','default');
      if ($tmpl == 'component') {
        return;
      }

  		// Parameters
      $position = $this->params->get('position');

  		switch ($position) {
  			// Top Left
  			case '0':
  				$class_position = "black-ribbon stick-top stick-left";
          $ribbon_src = "data:image/png;base64,";
  				break;
  			// Top Right
  			case '1':
  				$class_position = "black-ribbon stick-top stick-right";
          $ribbon_src = "data:image/png;base64,";
  				break;
        // Bottom Left
  			case '2':
  				$class_position = "black-ribbon stick-bottom stick-left";
          $ribbon_src = "data:image/png;base64,";
  				break;
  			// Bottom Right
  			case '3':
  				$class_position = "black-ribbon stick-bottom stick-right";
          $ribbon_src = "data:image/png;base64,";
  				break;
  			// Default Top Left
  			default:
  				$class_position = "black-ribbon stick-top stick-left";
          $ribbon_src = "data:image/png;base64,";
  				break;
  		}

  		$ribbon_out ='<img src="'.$ribbon_src.'" class="'.$class_position.'" alt="Black Ribbon" />';
  		$buffer = JResponse::getBody();
			// Position Top
			if ($position == 0 || $position == 1) {
        $buffer = preg_replace('/<body(.*?)>/i', '<body$1>'.$ribbon_out, $buffer, 1);
			}
			// Position Bottom
			elseif ($position > 1) {

        $parts = explode('</body>', $buffer);
				if (sizeof($parts)<2) {
					return;
				}

				$parts[sizeof($parts)-2] .= $ribbon_out;
				$buffer = implode('</body>', $parts);
			}

  		JResponse::setBody($buffer);
  	}
  }
}